using System;using System.Collections.Generic;using Geladeria2;public class Program{    public static void Main()    {        var geladeira = new Geladeira();        geladeira.ImprimirItens();        geladeira.Andares[0].Containers[0].AdicionarItem(0, "Maçã");        geladeira.Andares[1].Containers[1].AdicionarItem(2, "Leite");        geladeira.Andares[2].Containers[1].AdicionarItem(3, "Presunto");        Console.WriteLine("\nApós adicionar alguns itens:");        geladeira.ImprimirItens();        geladeira.Andares[0].Containers[0].RemoverItem(0);        Console.WriteLine("\nApós remover um item:");        geladeira.ImprimirItens();    }}